import { TodosActiobTypes, TodosAction, TodosState } from "../../types/todo"

const initialState: TodosState = {
	todos: [],
	loading: false,
	error: null
}

export const todosReducer = (state = initialState, action: TodosAction): TodosState => {
	switch (action.type){
		case TodosActiobTypes.FETCH_TODOS:
			return {loading: true, error: null, todos: []}
		case TodosActiobTypes.FETCH_TODOS_SUCCESS:
			return {loading: false, error: null, todos: action.payload}
		case TodosActiobTypes.FETCH_TODOS_ERROR:
			return {loading: false, error: action.payload, todos: []}
		default:
			return state
	}
}
