import React,  {useState, useEffect, useRef} from "react";
import { ITodo } from "../Interfaces";

type TodoPopupProps = {
	isOpen: boolean,
	onChange(title: string): void,
	todo: ITodo
}


export const TodoPopup: React.FC<TodoPopupProps> = ({isOpen, onChange, todo}) => {

	const ref = useRef<HTMLInputElement>(null);


	const handleSubmit = (event: React.MouseEvent) => {
		event.preventDefault();

		onChange(
			ref.current!.value
		);

	}

	const handleChangeTodo = (e: ChangeEvent<HTMLInputElement>) =>{
		const newValue = e.target.value;
	}

	const classes = ['popup'];


	if (isOpen) {
		classes.push('popup_opened');
	};


	return (

		<div className={classes.join(' ')}>
			<div className="popup__container">
				<div className="popup__content">
				<label className="todo">
							{/* <input 
								type="checkbox" 
								// checked={todo.completed}
								// onChange={onToggle.bind(null, todo)}
								id="checkbox"
							/> */}
							<input 
							ref={ref} 
							type="text" 
							name="edit"  
							id="title" 
							className="" 
							onChange={handleChangeTodo}
							required 
							value={todo.title}
							/>
							{/* <span>fcs</span> */}
							<div className="">
								<i className="material-icons px1"
								// onClick={event => editHandler(event, todo)}
								>edit</i>
								<i className="material-icons red-text" 
								// onClick={event => removeHandler(event, todo)}
								>delete</i>

							</div>
				</label>

				</div>


			</div>


		</div>
	);

}
