import React,  {useState, useEffect, useRef, ChangeEvent} from "react";
import { ITodo } from "../../../Interfaces";
import "./TodoPopup.css";

type TodoPopupProps = {
	isOpen: boolean,
	onChange(title: string): void,
	onClose: () => void,
	todo: ITodo
}


export const TodoPopup: React.FC<TodoPopupProps> = ({isOpen, onChange, todo, onClose}) => {

	// const ref = useRef<HTMLInputElement>(null);
	const [task, setTitle] = useState<string>('');
	// const [task, setTask] = useState<string>("");

	const handleChange = (event: ChangeEvent<HTMLInputElement>): void => {

		setTitle(event.target.value);

  };

	// const handleChangeTodo = (event: React.ChangeEventHandler<HTMLFormElement>) => {
	// 	// event.preventDefault();
	// 	// const enteredTitle = event.target.value;
	// 	setTitle(event.);
	// }

	// console.log(ref.current!.value)

	useEffect(() => {
		// ref.current!.value = todo.title
		setTitle(todo.title)
	}, [isOpen]);

	const classes = ['popup'];
	if (isOpen) {
		classes.push('popup_opened');
	};

	const keyPressHandler = (event: React.KeyboardEvent) => {
		console.log(event)
		event.preventDefault();
		if (event.key === 'Enter'){
			onChange(title);
			onClose();
			setTitle('');
		}
	}

	return (

		<div className={classes.join(' ')}>
			<div className="popup__container">
				<div className="popup__content">
				<button className="popup__close" aria-label="Close" type="button" onClick={onClose}/>
				<label className="todos">
					<input
							type="text"
							placeholder="Task..."
							name="task"
							value={task}
							onChange={handleChange}
						/>
						{/* <input 
							onKeyPress={keyPressHandler}
							// ref={ref}
							type="text" 
							id="title" 
							placeholder="Введите название дела"
							value={title || ''}
							onChange={event => handleChangeTodo(event)}
							/> */}
							{/* <input 
							ref={ref} 
							title="Измени значение поля и нажми Enter"
							type="text" 
							id="title" 
							required 
							onKeyPress={keyPressHandler}
							value={title || ''}
							onChange={handleChangeTodo}
							/> */}
							Измени значение поля и нажми 	&#8629; Enter
				</label>

				</div>


			</div>


		</div>
	);

}
