import { useTypedSelector } from "../hook/useTypesSelector";
import { ITodo } from '../Interfaces';
import { useActions } from "../hook/useActions";

type TodoListProps = {
	// todoss: ITodo[]
	// onToggle(id: number): void
	// onRemove(id: number): void
}

export const TodoList: React.FC<TodoListProps> = ({
	// todoss,
	// onToggle,
	// onRemove
}) => {
	// const {todos, loading, error, page, limit} = useTypedSelector(state => state.todo)
	// const {fetchTodos, setTodoPage} = useActions();
	const pages = [1,2,3,4,5]

	useEffect(()=>{
		fetchTodos(page, limit);
	}, [page])

	if (loading) {
		return <h1>Идет загрузка...</h1>
	}
	if (error) {
		return <h2>{error}</h2>
	}

if (todos.length === 0) {
	return <p className="center">Пока дел нет</p>
}


const removeHandler = (event: React.MouseEvent, id: number) => {
	event.preventDefault();
	onRemove(id);
}

	return (
		<>
		<ul>
			{todos.map(todo => {
				const classes = ['todo']
				if (todo.completed) {
					classes.push('completed');
				}

				return (
					<li className={classes.join(' ')} key={todo.id}>
						<label>
							<input 
							type="checkbox" 
							checked={todo.completed}
							onChange={onToggle.bind(null, todo.id)}
							/>
							<span>{todo.title}</span>
							<i className="material-icons red-text" 
							onClick={event => removeHandler(event, todo.id)}
							>delete</i>
						</label>
					</li>
				)
			})}
		</ul>
		<ul className="pagination row">
			{pages.map(p => (
				<li
					className={`col s1 ${p === page ? "active" : "waves-effect"}`}
					key={p}
					// style={{border: p === page ? '2px solig green' : '1px solid gray', padding: '10px'}}
					onClick={() => setTodoPage(p)}			
				>{p}
				</li>
			))}
		</ul>
		</>
	)
}
