import React,  {useState, useEffect, useRef, ChangeEvent} from "react";
import { ITodo } from "../Interfaces";

type TodoPopupProps = {
	isOpen: boolean,
	onChange(title: string): void,
	todo: ITodo
}


export const TodoPopup: React.FC<TodoPopupProps> = ({isOpen, onChange, todo}) => {

	const ref = useRef<HTMLInputElement>(null);
	const [title, setTitle] = useState(todo.title)

	const handleChangeTodo = (e: ChangeEvent<HTMLInputElement>) =>{
		setTitle(e.target.value);
	}

	const classes = ['popup'];
	if (isOpen) {
		classes.push('popup_opened');
	};

	const keyPressHandler = (event: React.KeyboardEvent) => {
		if (event.key === 'Enter'){
			onChange(ref.current!.value);
			// ref.current!.value = '';
		}
	}

	return (

		<div className={classes.join(' ')}>
			<div className="popup__container">
				<form className="popup__content">
				<label className="todo">
							{/* <input 
								type="checkbox" 
								// checked={todo.completed}
								// onChange={onToggle.bind(null, todo)}
								id="checkbox"
							/> */}
							<input 
							ref={ref} 
							type="text" 
							name="edit"  
							id="title" 
							className="" 
							required 
							onKeyPress={keyPressHandler}
							value={title || ''}
							onChange={() => handleChangeTodo(event)}
							/>
							<div className="">
								<i className="material-icons px1"
								// onClick={event => editHandler(event, todo)}
								>edit</i>
								<i className="material-icons red-text" 
								// onClick={event => removeHandler(event, todo)}
								>delete</i>

							</div>
				</label>

				</form>


			</div>


		</div>
	);

}
