import React,  {useState, useEffect} from "react";

type TodoPopupProps = {
	isOpen: boolean,
}


export const TodoPopup: React.FC<TodoPopupProps> = ({isOpen}) => {

	const avatarRef = React.useRef();

	useEffect(() => {
		avatarRef.current.value = '';
	}, [props.isOpen]);

	function handleSubmit(e) {
		e.preventDefault();

		props.onUpdateAvatar({
			/* Значение инпута, полученное с помощью рефа */
			avatar: avatarRef.current.value
		});

	}

	const classes = ['popup'];


	if (isOpen) {
		classes.push('popup_opened');
	};

console.log(isOpen)
	return (

		<div className={classes.join(' ')}>
			<PopupWithForm  onSubmit={handleSubmit} isOpen={props.isOpen} onClose={props.onClose}>
			<input id="avatar-input" ref={avatarRef} type="url" placeholder="Аватар" name="avatar" className="popup__field-avatar popup-input" required />
			<span className="avatar-input-error popup__input-error" />
		</PopupWithForm>

		</div>
	);

}
